Ti.Map=require("ti.map"),module.exports=function(){if(!Ti.Android)return!0;if(Ti.Platform.model.match(/Kindle/i))return Ti.UI.createNotification({message:"Der "+Ti.Platform.model+" unterstützt leider keine Karten"}).show(),!1;var e=Ti.Map.isGooglePlayServicesAvailable();if(e!=Ti.Map.SUCCESS){var t="GooglePlayService ";switch(e){case Ti.Map.SERVICE_MISSING:t+="ist nicht installiert.";break;case Ti.Map.SERVICE_VERSION_UPDATE_REQUIRED:t+="ist nicht in aktueller Version.";break;case Ti.Map.SERVICE_DISABLED:t+="ist abgeschaltet.";break;case Ti.Map.SERVICE_INVALID:t+="kann sich nicht bei Google anmelden.";break;default:alert("Unbekannter Fehler.")}t+="\nDeswegen kann keine Karte angezeigt werden. Bitte installieren Sie die neueste Version von GMS.\n\nGegebenenfalls müssen Sie ein GoogleKonto anlegen.";var i=Ti.UI.createAlertDialog({message:t,ok:"Ok",androidView:Ti.UI.createImageView({image:"/images/gms.png",width:Ti.UI.FILL,height:"auto"}),title:"Problem mit GoogleMaps"});i.show(),i.addEventListener("click",function(){try{Ti.Android.currentActivity.startActivity(Ti.Android.createIntent({action:Ti.Android.ACTION_VIEW,data:"market://details?id=com.google.android.gms"}))}catch(e){}})}return e==Ti.Map.SUCCESS?!0:!1};